import React, { useContext, useState } from "react";
import { Link } from 'react-router-dom'
import jobContext from "../../context/jobs/jobsContext";

import "./css/JobNuevo.css";

export default function JobNuevo() {
  const jobsContext = useContext(jobContext);
  const { agregarJobs } = jobsContext;

  const [job, guardarJob] = useState({
    company: "",
    title: "",
    logo: "",
    background: "",
    techs: "",
    description: "",
    begin: "",
    end: "",
  });

  const { company, title, logo, description, background, techs, begin, end } =
    job;

  const [error, guardarError] = useState(false);

  const onChange = (e) => {
    const { name, value, files } = e.target;
    guardarJob({
      ...job,
      [name]: files ? files[0] : value,
    });
  };

  const onSubmit = (e) => {
    e.preventDefault();

    if (
      company.trim() === "" ||
      title.trim() === "" ||
      description.trim() === "" ||
      begin.trim() === "" ||
      end.trim() === ""
    ) {
      guardarError(true);
    }

    guardarError(false);

    // Coudinary
    const data = new FormData();
    data.append("file", job.logo);
    data.append("upload_preset", "portfolio-aitor");
    data.append("cloud_name", "aitorcloud");
    fetch("https://api.cloudinary.com/v1_1/aitorcloud/image/upload", {
      method: "post",
      body: data,
    })
      .then((res) => res.json())
      .then((file) => {
        console.log(file.secure_url);
        console.log(file);

        // Agrego el producto
        agregarJobs({
          company,
          title,
          logo: file.secure_url,
          description,
          background,
          techs,
          begin,
          end,
        });
      })
      .catch((err) => {
        console.log(err);
      });

    guardarJob({
      company: "",
      title: "",
      logo: "",
      description: "",
      background: "",
      techs: "",
      begin: "",
      end: "",
    });
    
  };

  return (
    <div className="proyect-form-container">
      <div className="proyect-form">
        <div className="title-container">
          New Job<i className="fas fa-user-tie"></i>
        </div>

        {error ? (
          <div className="red-error">You must complete all the fields</div>
        ) : null}

        <form onSubmit={onSubmit}>
          <div className="cajetin-form">
            <input
              className="line-form"
              type="text"
              name="company"
              placeholder="empresa"
              onChange={onChange}
            />
          </div>

          <div className="cajetin-form">
            <input
              className="line-form"
              type="text"
              name="title"
              placeholder="puesto"
              onChange={onChange}
            />
          </div>

          <div className="cajetin-form">
            <label className="custom-file-upload">
              Select File <i className="far fa-file"></i>
              <input
                className="line-form"
                type="file"
                name="logo"
                placeholder="imagen empresa"
                onChange={onChange}
              />
            </label>
          </div>

          <div className="cajetin-form">
            <input
              className="line-form"
              type="text"
              name="description"
              placeholder="descripciÃ³n"
              onChange={onChange}
            />
          </div>

          <div className="cajetin-form">
            <input
              className="line-form"
              type="text"
              name="background"
              placeholder="company background"
              onChange={onChange}
            />
          </div>

          <div className="cajetin-form">
            <input
              className="line-form"
              type="text"
              name="techs"
              placeholder="techs"
              onChange={onChange}
            />
          </div>

          <div className="cajetin-form">
            <input
              className="line-form"
              type="date"
              name="begin"
              placeholder="incicio"
              onChange={onChange}
            />
          </div>

          <div className="cajetin-form">
            <input
              className="line-form"
              type="date"
              name="end"
              placeholder="finalizado"
              onChange={onChange}
            />
          </div>

          <button className="form-button" type="submit" value="Subir Proyecto">
            <i className="fas fa-arrow-circle-up" alt="upload project"></i>
          </button>
        </form>
      </div>
    </div>
  );
}
